import React from "react";

class Update extends React.Component {

    constructor (props) {
        super(props);
        this.state = {
            description: '',
            deadline: '',
            done: false
        }
        this.handleDescription = this.handleDescription.bind(this);
        this.handleDeadline = this.handleDeadline.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDescription (event) {
      this.setState({
          description: event.target.value,
          deadline: this.state.deadline,
          done: false
      })
    }

    handleDeadline (event) {
        this.setState({
            description: this.state.description,
            deadline: event.target.value,
            done: false
        })
    }

    handleSubmit (event) {
        event.preventDefault();
    }


    render () {
        const {text, date} = this.props;

        return(
                <form onSubmit={this.handleSubmit}>
                    <input className="inputTodo description" type="text" onChange={this.handleDescription} value={text}/>
                    <input className="inputTodo deadline" type="text" onChange={this.handleDeadline} placeholder={date}/> 
                    <button className="inputTodo myButton" type="submit">update</button>
                    <button className="inputTodo myButton" type="submit">cancel</button>
                </form>
        )
    }
}

export default Update;
