import React from "react";
import changeDateFormat from './change-format-date.js';

class Todo extends React.Component {
    onCheckBoxChange = (event) => {
        if(event.target.checked) {
            this.props.changeDone(true, this.props.index) 
            // console.log('true')  
        } else {
            this.props.changeDone(false, this.props.index)  
            // console.log('false')
        }
    }

    render () {
        const {id, text, date, checklist} = this.props;
        if ({id} === undefined) {
            console.log('undefined')
            return(
                <li>No items...</li>
            )
        } else {
            console.log('defined')
            return(
                <li id={'todo'+id}>
                    <label>
                        <input type="checkbox" className="checkbox" onChange={this.onCheckBoxChange} checked={checklist}/>
                        {text} - {changeDateFormat(date)}
                    </label>
                </li>
            )
        }
    }
}

export default Todo;
