import React from "react";
// import formatDate from '../util/formatDate.js';

class Avg extends React.Component {
    constructor (props) {
        super(props);
        this.state = {
            days: '',
            calculatedDays: ''
        }
        this.handleDays = this.handleDays.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDays (event) {
      this.setState({
          days: event.target.value
      })
    }

    calculate(){
        this.setState({
            calculatedDays: this.state.days
        })
    }

    handleSubmit (event) {
        event.preventDefault();
        this.setState({
            days: '',
            calculatedDays: ''
        })
        event.target.reset()     
    }

    render () {
        const {houses} = this.props;
        const cityArr = houses.map(item => item.city)
        const cities = cityArr.filter((item, pos) => {
            return cityArr.indexOf(item) === pos;
        })

        const cityTh = cities.map((item, key)=> 
        <th key={key}>{item}</th>
        )

        // const itemJson = houses;
        // console.log(itemJson)

        return(
            <div>
                <div>
                    Take the average of last
                    <form onSubmit={this.handleSubmit}>
                        <input className="inputTodo" type="text" onChange={this.handleDays} placeholder=""/>
                        <button className= "inpButton" type="submit" onClick={()=>addNewTodo(this.state)}>calculate</button>
                    </form>
                </div>

                <table>
                    <thead>
                        <tr>
                            {cityTh}
                        </tr>
                    </thead>
                    <tbody>
                        <tr className= 'textFormat'>

                        </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Avg;
