import React, { Component } from 'react';
import todoLogo from './todo.png';
import './App.css';
import Todo from "./todo.js";
import data from './todoData.json';

class App extends Component {

  state = {
    data: []
  };

  constructor(){
    super();
    console.log('Constructor');
  };

  componentDidMount() {
    console.log("app: mount");
    this.setState({
      data: data
    })
  };

  componentDidUpdate() {
    console.log("app: update");
  }

  shouldComponentUpdate() {
    console.log("app: should update");
    return true;
  }

  changeDone = (boolean, index) => {
    const newState = this.state;
    newState.data[index].done = boolean;
    this.setState(newState);
  }

  render() {
    console.log(data)
    const elements = this.state.data.map((item, key)=> <Todo
      key={key}
      id={item.id}
      text={item.description}
      date={item.deadline}
      checklist={item.done}
      />)
    return (

      <div className="App">
        <header className="App-header">

          <img src={todoLogo} className="todo-logo" alt="todo" />

          <ul>
            {elements}
          </ul>

        </header>
      </div>
    );
  }
}

export default App;
