import React from "react";

class Add extends React.Component {

    constructor (props) {
        super(props);
        this.state = {
            description: '',
            deadline: '',
            done: false
        }
        this.handleDescription = this.handleDescription.bind(this);
        this.handleDeadline = this.handleDeadline.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleDescription (event) {
      this.setState({
          description: event.target.value
      })
    }

    handleDeadline (event) {
        this.setState({
            deadline: event.target.value
        })
    }

    handleSubmit (event) {
        event.preventDefault();
        this.setState({
            description: '',
            deadline: ''
        })
        event.target.reset()     
    }

    render () {
        const {addNewTodo, wrongInput} = this.props;
        const jsonFileFormat = {"link":"string - not null","location":{"country":"string - not null","city":"string - not null","address":"string - not null","coordinates":{"lat":"number","lng":"number"}},"size":{"parcel_m2":"number","gross_m2":"number","net_m2":"number - not null","rooms":"number - not null"},"price":{"value":"number - not null","currency":"string - not null"},"description":"string","title":"string","images":"[string(url), string(url)]"};

        return(
            <div>
                <div className="entryFormat">
                    <h3>Please make sure that</h3>
                    <ul>
                        <li>
                            Your JSON file must be an array.
                        </li>
                        <li>
                            Every element in the array must be an object.
                        </li>
                        <li>
                            Every object must be in below format
                            {JSON.stringify(jsonFileFormat)}

                        </li>
                    </ul>
                </div>

                <form onSubmit={this.handleSubmit}>
                    <input className="inputTodo description" type="text" onChange={this.handleDescription} placeholder=" what to do"/>
                    <input className="inputTodo deadline" type="text" onChange={this.handleDeadline} placeholder=" when to do (yyyy-mm-dd)"/> 
                    <button className= {wrongInput ? "inputTodo wrongInput" : "inputTodo"} type="submit" onClick={()=>addNewTodo(this.state)}>Add</button>
                </form>
            </div>
        )
    }
}

export default Add;
