import React from "react";
import changeDateFormat from './change-format-date.js';

class Todo extends React.Component {
    state = {
        done: false
    }

    onCheckBoxChange = (event) => {
        console.log(event.target.checked)
        // if(event.target.checked) {
        //     this.setState({
        //         'done': false
        //     })   
        // } else {
        //     this.setState({
        //         'done': true
        //     })   
        // }
        this.props.changeDone(this.state.done, this.props.id-1)
    }

    render () {
        const {id, text, date, checklist} = this.props;
        return(
                <li id={'todo'+id}>
                    <label>
                    {text} - {changeDateFormat(date)}
                    <input type="checkbox" class="checkbox" onChange={this.onCheckBoxChange} checked={checklist}/>
                    </label>
                </li>
        )
    }
}

export default Todo;
