import React from "react";

class Add extends React.Component {

    constructor (props) {
        super(props);
        this.state = {
            description: '',
            deadline: '',
            done: false
        }
        this.handleDescription = this.handleDescription.bind(this);
        this.handleDeadline = this.handleDeadline.bind(this);
    }

    handleDescription (event) {

      this.setState({
          description: event.target.value,
          deadline: this.state.deadline,
          done: false
      })
    }

    handleDeadline (event) {

        this.setState({
            description: this.state.description,
            deadline: event.target.value,
            done: false
        })
        console.log(this.state)
      }


    render () {
        const {addNewTodo} = this.props;

        return(
                <form onSubmit={()=>addNewTodo(this.state)}>
                    <input type="text" value={this.state.description} onChange={this.handleDescription} placeholder="what to do"/>
                    <input type="text" value={this.state.deadline} onChange={this.handleDeadline} placeholder="when to do (dd-mm-yyyy)"/> 
                    <button type="submit">Add</button>
                </form>
        )
    }
}

export default Add;
