import React from "react";
import changeDateFormat from '../util/change-format-date.js';

class Todo extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          done: true
        };
    }

    render () {
        const {id, text, date, done, changeDone, deleteTodo, index, editButton} = this.props;
        const listLink = `https://hyf-react-api.herokuapp.com/todos/${id}`;

        return(
                <li>
                    <label className= {done ? 'textFormat' : null}>
                        <input type="checkbox" className="checkbox" onClick={`location.href= ${listLink}`} onChange={()=>changeDone(id)} defaultChecked={this.props.done}/>
                        {(text !== '') ? text : 'no input!'} - {changeDateFormat(date)}
                    </label>
                    <p className="deleteButton" onClick={()=>deleteTodo(index)}>X</p>
                    <p className="editButton" onClick={()=>editButton(index)}>edit</p>
                </li>
        )
    }
}

export default Todo;
