import React from "react";
import changeDateFormat from './change-format-date.js';

class Todo extends React.Component {
    state = {
        done: false
    }

    onCheckBoxChange = (event) => {
        console.log(event.target.value);
        console.log(this.state);
        if(event.target.checked) {
            this.setState({
                'done': true
            })   
        } else {
            this.setState({
                'done': false
            })   
        }
        this.props.changeDone(this.state.done, this.props.index)
    }

    render () {
        const {id, text, date, checklist} = this.props;
        return(
                <li id={'todo'+id}>
                    <label>
                    {text} - {changeDateFormat(date)} - {checklist ? 'a': 'b'}
                    <input type="checkbox" class="checkbox" onChange={this.onCheckBoxChange}    />
                    </label>
                </li>
        )
    }
}

export default Todo;
