import React from "react";
import changeDateFormat from './change-format-date.js';

class Todo extends React.Component {
    state = {
        done: false
    }

    onCheckBoxChange = (event) => {
        this.setState({
            [event.target.done]: true
        })
    }

    render () {
        const {id, text, date} = this.props;
        return(
                <li id={'todo'+id}>
                    <label>
                    {text} - {changeDateFormat(date)}
                    <input type="checkbox" class="checkbox" onChange={this.onCheckBoxChange}/>
                    </label>
                </li>
        )
    }
}

export default Todo;
