import React from "react";

class Add extends React.Component {

    constructor (props) {
        super(props);
        this.state = {
            description: '',
            deadline: '',
            done: false
        }
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange (event) {
        console.log(event.target.value)
      this.setState({
          description: event.target.description,
          deadline: event.target.deadline,
          done: false
      })
    }


    render () {
        const {addNewTodo} = this.props;

        return(
                <form onSubmit={()=>addNewTodo(this.state)}>
                    <input type="text" name='description' value={this.state.description} onChange={this.handleChange} placeholder="what to do"/>
                    <input type="text" name='deadline' value={this.state.deadline} onChange={this.handleChange} placeholder="when to do (dd-mm-yyyy)"/> 
                    <button type="submit">Add</button>
                </form>
        )
    }
}

export default Add;
