import React from "react";

class Add extends React.Component {

    handleSubmit

    render () {
        const {description, deadline} = this.props;

        return(
                <form onSubmit={this.handleSubmit} method='post' action='/todos' enctype="application/json">
                    <input type="text" placeholder="what to do"/>
                    <input type="text" name="when to do (dd-mm-yyyy)"/> 
                    <button type="submit" formmethod="post">Add</button>
                </form>
        )
    }
}

export default Add;
