import {
    configure,
    observable,
    action,
    runInAction
} from 'mobx';
// import axios from "axios";

configure({
    enforceActions: 'observed'
});

class houseApp {

    @observable output = {
        houses: [],
        status: 'loading'
    }

    @action async listHouses() {
        this.output.houses = [];
        this.output.status = 'loading';
        try {
            const data = await this.getItems();
            runInAction(() => {
                this.output.houses = data.results;
                this.output.status = 'done';
            })
        } catch (error) {
            runInAction(() => {
                this.output.status = 'error';
            })
        }
    }

    getItems() {
        return fetch('/houses')
            .then(response => response.json())
    }

    @action addNewTodo = (newEntry) => {
        // const newEntryJSON = JSON.parse(newEntry);
        this.output.status = 'loading';
        this.createNewItem(newEntry).then(() => {
                runInAction(() => {
                    this.output.houses.push(newEntry);
                    this.output.status = 'done';
                })
            })
            .catch((error) => {
                runInAction(() => {
                    this.output.status = 'error';
                })
            })
    }

    createNewItem(item) {
        return fetch('/houses/create', {
                method: 'POST',
                body: JSON.parse(item),
                headers: {
                    "Content-type": "application/json; charset=UTF-8"
                }
            })
            .then(response => response.json())
    }
}

const myHouseApp = new houseApp();

export default myHouseApp;
