import React, { Component } from 'react';
import todoLogo from './todo.png';
import './App.css';
import Todo from "./todo.js";
import data from './todoData.json';

class App extends Component {

  state = {
    data: data
  };

  changeDone = (condition, index) => {
    const newState = this.state;
    newState.data[index].done = condition;
    this.setState(newState);
    console.log(newState.data[index])
  }

  render() {
    const elements = this.state.data.map((item, key)=> <Todo
      key={key}
      index={key}
      changeDone = {this.changeDone}
      id={item.id}
      text={item.description}
      date={item.deadline}
      checklist={item.done}
      />)
    return (

      <div className="App">
        <header className="App-header">

          <img src={todoLogo} className="todo-logo" alt="todo" />

          <ul>
            {elements}
          </ul>

        </header>
      </div>
    );
  }
}

export default App;
