function specificDate(strDate) {
    const newDate = new Date(strDate);

    const options = {
        weekday: 'short',
        month: 'short',
        day: 'numeric',
        year: 'numeric'
    };

    return newDate.toLocaleDateString('en-US', options);
}

// console.log(specificDate('2015-12-23'));
export default specificDate;
