import React from "react";
import changeDateFormat from './change-format-date.js';

class Todo extends React.Component {


    render () {
        const {id, text, date, done, changeDone, index} = this.props;

        const changeCssFormat = event.target.checked ? 'doneFormat' : null;

        return(
                <li id={'todo'+id}>
                    <label>
                        <input type="checkbox" className="checkbox" className={changeCssFormat} onChange={()=>changeDone(index)} defaultChecked={done}/>
                        {text} - {changeDateFormat(date)}
                    </label>
                </li>
        )
    }
}

export default Todo;
