import {configure, observable, action, runInAction} from 'mobx';
// import axios from "axios";

configure({enforceActions: 'observed'});

class houseApp {

    @observable output = {
        houses : [],
        status : 'loading'
    }

    @action async listHouses() {
        this.output.houses = [];
        this.output.status = 'loading';
        try {
            const data = await this.getItems();
            runInAction(() => {
                this.output.houses = data.results;
                this.output.status = 'done';
            })
        }
        catch(error) {
            runInAction(() => {
                this.output.status = 'error';
            }) 
        }
    }

    getItems() {
        return fetch('/houses')
            .then(response => response.json())
    }

    @action addNewTodo = (newEntry) => {
        const newEntryJSON = JSON.parse(newEntry)
        console.log(newEntryJSON);
        console.log(typeof newEntryJSON);
        console.log("It is an array => " + Array.isArray(newEntryJSON));
        console.log(newEntryJSON[0])

        // if(newEntry.description === '' || !dateRegExp.test(newEntry.deadline)) {
        //     this.wrongInput = true
        // } else {
            this.output.status = 'loading';
            this.createNewItem(newEntryJSON).then(()=>{
                runInAction(() => {
                    newEntry.id = Math.random().toString(36).substr(2, 12);
                    newEntry.deadline = new Date(newEntry.deadline).toJSON();
                    this.data.todos.push(newEntry);
                    this.data.status = 'done';
                    this.wrongInput = false;
                })
            })
            .catch((error) => {
                runInAction(() => {
                    this.data.status = 'error';
                }) 
            })
        // }
    }

    createNewItem(item) {
        return fetch('https://hyf-react-api.herokuapp.com/todos/create', {
            method: 'POST',
            body: JSON.stringify(item),
            headers: {
              "Content-type": "application/json; charset=UTF-8"
            }
          })
          .then(response => response.json())
    }
}

const myHouseApp = new houseApp();

export default myHouseApp;
