import React from "react";
// import specificDate from './specific-date.js';

class Todo extends React.Component {
    render () {
        const {item, date} = this.props;
        function specificDate(strDate) {
            const options = {
                weekday: 'short',
                month: 'short',
                day: 'numeric',
                year: 'numeric'
            };

            return strDate.toLocaleDateString('en-US', options);
        }
        return(
            <ul>
                <li>{item}, {specificDate(date)}</li>
            </ul>
        )
    }
}

export default Todo;
